mapper.update:
update tbl_csm_user
user_id = #{userId} , user_name = #{userName} , password = #{password} , email = #{email} , tenant_id = #{tenantId} , role_name = #{roleName} , delete_flag = #{deleteFlag} , delete_time = #{deleteTime} ,
where user_id=#{userId}

